        /// <summary>
        /// 获取文件编码格式
        /// </summary>
        /// <param name="filePath">文件路径</param>
        /// <returns>编码格式</returns>
        public static Encoding GetFileEncoding(string filePath)
        {
            using (var stream = new FileStream(filePath, FileMode.Open, FileAccess.Read))
            {
                byte[] bom = new byte[4];
                stream.Read(bom, 0, 4);

                if (bom[0] == 0xEF && bom[1] == 0xBB && bom[2] == 0xBF)
                {
                    return Encoding.UTF8; // UTF-8 with BOM
                }
                else if (bom[0] == 0xFE && bom[1] == 0xFF)
                {
                    return Encoding.BigEndianUnicode; // UTF-16 BE
                }
                else if (bom[0] == 0xFF && bom[1] == 0xFE)
                {
                    return Encoding.Unicode; // UTF-16 LE
                }
                else
                {
                    // No BOM, detect encoding based on content
                    stream.Seek(0, SeekOrigin.Begin);
                    return GetStreamEncoding(stream);
                }
            }
        }

        /// <summary>
        /// 获取字节流编码
        /// </summary>
        /// <param name="stream">字节流</param>
        /// <returns>编码格式</returns>
        private static Encoding GetStreamEncoding(Stream stream)
        {
            byte[] buffer = new byte[4096];
            int bytesRead = stream.Read(buffer, 0, buffer.Length);
            char[] strGBK = Encoding.GetEncoding("GBK").GetString(buffer).ToCharArray();
            char[] strBIG5 = Encoding.GetEncoding("BIG5").GetString(buffer).ToCharArray();

            bool isGBK = IsCharsGBK(strGBK);
            bool isBIG5 = IsCharsBig5(strBIG5);

            if (isGBK && isBIG5)
            {
                long sizeGBK = GetCommonCharWeight(strGBK);
                long sizeBIG5 = GetCommonCharWeight(strBIG5);
                Console.WriteLine("Encode GBK is " + isGBK.ToString() + ", size:" + sizeGBK);
                Console.WriteLine("Encode BIG5 is " + isBIG5.ToString() + ", size:" + sizeBIG5);
                if (sizeBIG5 > 0 && sizeBIG5 > sizeGBK)
                {
                    return Encoding.GetEncoding("BIG5");
                }
                else if(sizeGBK > 0 && sizeGBK > sizeBIG5)
                {
                    return Encoding.GetEncoding("GBK");
                }
                else
                {
                    return Encoding.UTF8;
                }
            }
            else if (IsCharsGBK(strGBK))
            {
                return Encoding.GetEncoding("GBK");
            }
            else if (IsCharsBig5(strBIG5))
            {
                return Encoding.GetEncoding("BIG5");
            }
            else
            {
                return Encoding.UTF8;
            }
        }

        /// <summary>
        /// 获取字符数组为常用字符的权重
        /// </summary>
        /// <param name="buffer">字符数组</param>
        /// <returns>权重值，数值越大表示常用字符越多，为负数表示乱码字符越多</returns>
        private static long GetCommonCharWeight(char[] buffer)
        {
            long cnt = 0;
            //Console.WriteLine(new string(buffer));
            foreach (char c in buffer)
            {
                if (c > 0xFF)
                {
                    if (STR_COMMON_CHAR.IndexOf(c) >= 0)
                    {
                        cnt++;
                    }
                    else
                    {
                        cnt--;
                    }
                    
                }
            }
            return cnt;
        }

        /// <summary>
        /// 判断全部字符是否在GBK编码范围内
        /// </summary>
        /// <param name="buffer">字符数组</param>
        /// <returns></returns>
        private static bool IsCharsGBK(char[] buffer)
        {
            int wordSize = 0;
            foreach (char word in buffer)
            {
                byte[] bytes = Encoding.GetEncoding("GBK").GetBytes(word.ToString());
                if (bytes.Length <= 1)  // if there is only one byte, it is ASCII code
                {
                    continue;
                }
                else
                {
                    byte byte1 = bytes[0];
                    byte byte2 = bytes[1];
                    if (byte1 >= 129 && byte1 <= 254 && byte2 >= 64 && byte2 <= 254)     //判断是否是GBK编码
                    {
                        wordSize++;
                        continue;
                    }
                    else
                    {
                        Console.WriteLine("no gbk: " + word);
                        return false;
                    }
                }
            }
            Console.WriteLine("GBK word size: " + wordSize.ToString());
            return true;
        }

        /// <summary>
        /// 判断全部字符是否在BIG5编码范围内
        /// </summary>
        /// <param name="buffer">字符数组</param>
        /// <returns></returns>
        private static bool IsCharsBig5(char[] buffer)
        {
            int wordSize = 0;
            foreach (char word in buffer)
            {
                byte[] bytes = Encoding.GetEncoding("Big5").GetBytes(word.ToString());
                if (bytes.Length <= 1)  // if there is only one byte, it is ASCII code
                {
                    continue;
                }
                else
                {
                    byte byte1 = bytes[0];
                    byte byte2 = bytes[1];
                    if ((byte1 >= 129 && byte1 <= 255) && ((byte2 >= 64 && byte2 <= 126) || (byte2 >= 161 && byte2 <= 254)))     //判断是否是Big5编码
                    {
                        wordSize++;
                        continue;
                    }
                    else
                    {
                        Console.WriteLine("no big5: " + word + ", code: " + Convert.ToInt32(word).ToString());
                        return false;
                    }
                }
            }
            Console.WriteLine("BIG5 word size: " + wordSize.ToString());
            return true;
        }
    }
}
